def num_potions(creature):
    if creature == "B":
        return 1
    elif creature == "C":
        return 3
    elif creature == "D":
        return 5
    else:
        return 0
    
def num_potions_in_pair(pair: str):
    addition = 2
    if 'x' in pair:
        addition = 0
    return num_potions(pair[0]) + num_potions(pair[1]) + addition


input = ""
pairs = [input[i:i+2] for i in range(0, len(input), 2)]
updated = list(map(num_potions_in_pair, pairs))

result = sum(updated)

print(result)