def num_potions(creature):
    if creature == "B":
        return 1
    elif creature == "C":
        return 3
    elif creature == "D":
        return 5
    else:
        return 0
    
def num_potions_in_triple(triple: str):
    count = triple.count('x')
    extra = 0
    if(count == 0):
        extra = 6
    elif(count == 1):
        extra = 2
    return num_potions(triple[0]) + num_potions(triple[1]) + num_potions(triple[2]) + extra


input = ""
triples = [input[i:i+3] for i in range(0, len(input), 3)]
updated = list(map(num_potions_in_triple, triples))

result = sum(updated)

print(result)